// src/components/PlayerMap.jsx

import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Circle, useMap, Popup } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import { io } from "socket.io-client";

// Fix Leaflet marker icons
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: "https://unpkg.com/leaflet@1.9.3/dist/images/marker-icon-2x.png",
  iconUrl: "https://unpkg.com/leaflet@1.9.3/dist/images/marker-icon.png",
  shadowUrl: "https://unpkg.com/leaflet@1.9.3/dist/images/marker-shadow.png",
});

// Create custom icons for players
const playerIcon = L.icon({
  iconUrl: "https://unpkg.com/leaflet@1.9.3/dist/images/marker-icon.png",
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
});

const otherPlayerIcon = L.icon({
  iconUrl: "https://maps.google.com/mapfiles/ms/icons/red-dot.png",
  iconSize: [32, 32],
  iconAnchor: [16, 32],
  popupAnchor: [0, -32],
});

// Server URL - Replace this with your actual server URL or IP address for mobile testing
// For example: "http://192.168.1.100:3001" if testing on your local network
const SERVER_URL = process.env.REACT_APP_SERVER_URL || "http://localhost:3001";

// Setup socket with explicit connection
const socket = io(SERVER_URL, {
  reconnectionAttempts: 5,
  timeout: 10000,
  transports: ["websocket", "polling"],
});

const RecenterMap = ({ position }) => {
  const map = useMap();
  useEffect(() => {
    if (position) {
      map.setView(position);
    }
  }, [position, map]);
  return null;
};

const PlayerMap = () => {
  const [position, setPosition] = useState(null); // Map center
  const [playerPosition, setPlayerPosition] = useState(null); // Current player
  const [playerId, setPlayerId] = useState(null); // Current player's ID
  const [allPlayers, setAllPlayers] = useState({}); // All players
  const [connected, setConnected] = useState(false); // Socket connection status
  const [playerName, setPlayerName] = useState(() => {
    // Use stored name or generate random one
    return localStorage.getItem("playerName") || `Player-${Math.floor(Math.random() * 1000)}`;
  });
  const watchIdRef = useRef(null);
  const mapRef = useRef(null);

  // Handle connection and socket events
  useEffect(() => {
    // Socket connection events
    socket.on("connect", () => {
      console.log("🔌 Connected to server with ID:", socket.id);
      setConnected(true);
      setPlayerId(socket.id);
      
      // If we already have a position, send it immediately on connect
      if (playerPosition) {
        socket.emit("updatePosition", {
          lat: playerPosition[0],
          lng: playerPosition[1],
          name: playerName
        });
      }
    });

    socket.on("disconnect", () => {
      console.log("❌ Disconnected from server");
      setConnected(false);
    });

    socket.on("connect_error", (err) => {
      console.error("Connection error:", err);
    });

    // Listen for players data
    socket.on("playersUpdate", (playersData) => {
      console.log("📡 Received players update:", playersData);
      setAllPlayers(playersData);
    });

    return () => {
      socket.off("connect");
      socket.off("disconnect");
      socket.off("connect_error");
      socket.off("playersUpdate");
    };
  }, [playerName, playerPosition]);

  // Initialize geolocation watching
  useEffect(() => {
    console.log("📍 Requesting location...");

    if (!navigator.geolocation) {
      alert("Geolocation not supported by your browser.");
      return;
    }

    // Store player name
    localStorage.setItem("playerName", playerName);

    // Watch user position and send to server
    watchIdRef.current = navigator.geolocation.watchPosition(
      (pos) => {
        const newPos = [pos.coords.latitude, pos.coords.longitude];
        console.log("📌 My position:", newPos);
        setPosition(newPos);
        setPlayerPosition(newPos);

        // Send to server if connected
        if (socket.connected) {
          socket.emit("updatePosition", {
            lat: pos.coords.latitude,
            lng: pos.coords.longitude,
            name: playerName
          });
        }
      },
      (err) => {
        console.error("❌ Geolocation error:", err);
        alert(`Geolocation error: ${err.message}`);
      },
      {
        enableHighAccuracy: true,
        timeout: 20000,
        maximumAge: 0,
      }
    );

    return () => {
      if (watchIdRef.current !== null) {
        navigator.geolocation.clearWatch(watchIdRef.current);
      }
    };
  }, [playerName]);

  // Function to detect if a position is close to the current player (to avoid comparing exact floats)
  const isCloseToPlayer = (lat, lng) => {
    if (!playerPosition) return false;
    
    // Small tolerance for float comparison (approx 1-2 meters)
    const tolerance = 0.00002; 
    
    return Math.abs(lat - playerPosition[0]) < tolerance && 
           Math.abs(lng - playerPosition[1]) < tolerance;
  };

  // Handle name change
  const handleNameChange = (e) => {
    const newName = e.target.value;
    setPlayerName(newName);
    localStorage.setItem("playerName", newName);
    
    // Update server with new name if we have a position
    if (playerPosition && socket.connected) {
      socket.emit("updatePosition", {
        lat: playerPosition[0],
        lng: playerPosition[1],
        name: newName
      });
    }
  };

  return (
    <div className="player-map-container">
      <div className="map-controls">
        <div className="status-indicator">
          <span className={connected ? "status-connected" : "status-disconnected"}>
            {connected ? "● Connected" : "● Disconnected"}
          </span>
        </div>
        <div className="player-name-container">
          <label htmlFor="playerName">Your Name: </label>
          <input
            type="text"
            id="playerName"
            value={playerName}
            onChange={handleNameChange}
            maxLength={20}
          />
        </div>
        {position && (
          <div className="player-coordinates">
            Lat: {position[0].toFixed(6)}, Lng: {position[1].toFixed(6)}
          </div>
        )}
        <div className="players-count">
          Players Online: {Object.keys(allPlayers).length}
        </div>
      </div>

      {position && playerPosition ? (
        <MapContainer
          center={position}
          zoom={18}
          minZoom={15}
          maxZoom={20}
          style={{ height: "80vh", width: "100%" }}
          ref={mapRef}
        >
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

          {/* Current player marker */}
          <Marker position={playerPosition} icon={playerIcon}>
            <Popup>
              <strong>You: {playerName}</strong><br />
              {playerPosition[0].toFixed(6)}, {playerPosition[1].toFixed(6)}
            </Popup>
          </Marker>

          {/* Circle around player */}
          <Circle center={playerPosition} radius={100} color="blue" weight={1} fillOpacity={0.1} />

          {/* Other players */}
          {Object.entries(allPlayers).map(([id, player]) => {
            if (!player || !player.lat || !player.lng || isCloseToPlayer(player.lat, player.lng)) {
              return null;
            }
            
            return (
              <Marker
                key={id}
                position={[player.lat, player.lng]}
                icon={otherPlayerIcon}
              >
                <Popup>
                  <strong>{player.name || `Player ${id.substring(0, 4)}`}</strong><br />
                  {player.lat.toFixed(6)}, {player.lng.toFixed(6)}
                </Popup>
              </Marker>
            );
          })}

          <RecenterMap position={position} />
        </MapContainer>
      ) : (
        <div className="loading-location">
          <p>📍 Loading location… Make sure location permission is allowed.</p>
        </div>
      )}

      <style jsx>{`
        .player-map-container {
          display: flex;
          flex-direction: column;
          height: 100%;
        }
        
        .map-controls {
          background-color: #f5f5f5;
          padding: 10px;
          display: flex;
          flex-wrap: wrap;
          justify-content: space-between;
          align-items: center;
          border-bottom: 1px solid #ddd;
        }
        
        .status-indicator {
          margin-right: 15px;
        }
        
        .status-connected {
          color: green;
          font-weight: bold;
        }
        
        .status-disconnected {
          color: red;
          font-weight: bold;
        }
        
        .player-name-container {
          display: flex;
          align-items: center;
          margin-right: 15px;
        }
        
        .player-name-container input {
          margin-left: 5px;
          padding: 4px 8px;
          border: 1px solid #ccc;
          border-radius: 4px;
        }
        
        .loading-location {
          display: flex;
          justify-content: center;
          align-items: center;
          height: 80vh;
          background-color: #f7f7f7;
          font-size: 18px;
        }
      `}</style>
    </div>
  );
};

export default PlayerMap;
