// src/components/PlayerMap.jsx

import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Circle, useMap } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import { io } from "socket.io-client";

// Fix Leaflet marker icons
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: "https://unpkg.com/leaflet@1.9.3/dist/images/marker-icon-2x.png",
  iconUrl: "https://unpkg.com/leaflet@1.9.3/dist/images/marker-icon.png",
  shadowUrl: "https://unpkg.com/leaflet@1.9.3/dist/images/marker-shadow.png",
});

// Setup socket
const socket = io("http://localhost:3001"); // change this if using LAN IP for mobile testing

const RecenterMap = ({ position }) => {
  const map = useMap();
  useEffect(() => {
    if (position) {
      map.setView(position);
    }
  }, [position, map]);
  return null;
};

const PlayerMap = () => {
  const [position, setPosition] = useState(null); // Map center
  const [playerPosition, setPlayerPosition] = useState(null); // Current player
  const [allPlayers, setAllPlayers] = useState({}); // All players
  const watchIdRef = useRef(null);

  useEffect(() => {
    console.log("üìç Requesting location...");

    if (!navigator.geolocation) {
      alert("Geolocation not supported.");
      return;
    }

    // Watch user position and send to server
    watchIdRef.current = navigator.geolocation.watchPosition(
      (pos) => {
        const newPos = [pos.coords.latitude, pos.coords.longitude];
        console.log("üìå My position:", newPos);
        setPosition(newPos);
        setPlayerPosition(newPos);

        // Send to server
        socket.emit("updatePosition", {
          lat: pos.coords.latitude,
          lng: pos.coords.longitude,
        });
      },
      (err) => {
        console.error("‚ùå Geolocation error:", err);
        alert(`Geolocation error: ${err.message}`);
      },
      {
        enableHighAccuracy: true,
        timeout: 20000,
        maximumAge: 0,
      }
    );

    return () => {
      if (watchIdRef.current !== null) {
        navigator.geolocation.clearWatch(watchIdRef.current);
      }
    };
  }, []);

  // Listen for players data
  useEffect(() => {
    socket.on("playersUpdate", (playersData) => {
      console.log("üì° Received players update:", playersData);
      setAllPlayers(playersData);
    });

    return () => {
      socket.off("playersUpdate");
    };
  }, []);

  return position && playerPosition ? (
    <div>
      <MapContainer
        center={position}
        zoom={18}
        minZoom={15}
        maxZoom={20}
        style={{ height: "80vh", width: "100%" }}
      >
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

        {/* Current player marker */}
        <Marker position={playerPosition} />

        {/* Circle around player */}
        <Circle center={playerPosition} radius={1000} color="blue" />

        {/* Other players */}
        {Object.entries(allPlayers).map(([id, loc]) => {
          if (!loc || (loc.lat === playerPosition[0] && loc.lng === playerPosition[1])) return null;
          return (
            <Marker
              key={id}
              position={[loc.lat, loc.lng]}
              icon={L.icon({
                iconUrl: "https://maps.google.com/mapfiles/ms/icons/red-dot.png",
                iconSize: [32, 32],
                iconAnchor: [16, 32],
              })}
            />
          );
        })}

        <RecenterMap position={position} />
      </MapContainer>
    </div>
  ) : (
    <p>üìç Loading location‚Ä¶ Make sure location permission is allowed.</p>
  );
};

export default PlayerMap;
