import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Circle, useMap } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: "https://unpkg.com/leaflet@1.9.3/dist/images/marker-icon-2x.png",
  iconUrl: "https://unpkg.com/leaflet@1.9.3/dist/images/marker-icon.png",
  shadowUrl: "https://unpkg.com/leaflet@1.9.3/dist/images/marker-shadow.png",
});

const RecenterMap = ({ position }) => {
  const map = useMap();
  useEffect(() => {
    if (position) {
      map.setView(position);
    }
  }, [position, map]);
  return null;
};

const PlayerMap = () => {
  const [position, setPosition] = useState(null);
  const [error, setError] = useState(null);
  const watchIdRef = useRef(null);

  const requestInitialPosition = () => {
    setError(null);
    if (!navigator.geolocation) {
      setError("Geolocation not supported.");
      return;
    }

    // Quick initial getCurrentPosition with high accuracy and shorter timeout
    navigator.geolocation.getCurrentPosition(
      (pos) => {
        const { latitude, longitude } = pos.coords;
        const initialPos = [latitude, longitude];
        setPosition(initialPos);
        startWatchPosition();
      },
      (err) => {
        setError(`Initial position error: ${err.message}`);
      },
      {
        enableHighAccuracy: true,
        timeout: 7000, // 7 seconds for initial fix
        maximumAge: 0,
      }
    );
  };

  const startWatchPosition = () => {
    if (watchIdRef.current !== null) {
      navigator.geolocation.clearWatch(watchIdRef.current);
    }

    watchIdRef.current = navigator.geolocation.watchPosition(
      (pos) => {
        const { latitude, longitude } = pos.coords;
        setPosition([latitude, longitude]);
      },
      (err) => {
        setError(`Watch position error: ${err.message}`);
      },
      {
        enableHighAccuracy: true,
        timeout: 20000, // longer timeout for continuous tracking
        maximumAge: 0,
      }
    );
  };

  useEffect(() => {
    requestInitialPosition();

    return () => {
      if (watchIdRef.current !== null) {
        navigator.geolocation.clearWatch(watchIdRef.current);
      }
    };
  }, []);

  return (
    <div>
      {!position && !error && <p>üìç Loading location‚Ä¶ Make sure location permission is allowed.</p>}
      {error && (
        <div>
          <p style={{ color: "red" }}>‚ùå {error}</p>
          <button onClick={requestInitialPosition}>Retry Location</button>
        </div>
      )}
      {position && (
        <MapContainer
          center={position}
          zoom={18}
          minZoom={15}
          maxZoom={20}
          style={{ height: "80vh", width: "100%" }}
        >
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
          <Marker position={position} />
          <Circle center={position} radius={1000} color="blue" />
          <RecenterMap position={position} />
        </MapContainer>
      )}
    </div>
  );
};

export default PlayerMap;
