// src/components/PlayerMap.jsx

import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Circle, useMap } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

// Fix Leaflet marker icons
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: "https://unpkg.com/leaflet@1.9.3/dist/images/marker-icon-2x.png",
  iconUrl: "https://unpkg.com/leaflet@1.9.3/dist/images/marker-icon.png",
  shadowUrl: "https://unpkg.com/leaflet@1.9.3/dist/images/marker-shadow.png",
});

// Helper component to recenter the map on position change
const RecenterMap = ({ position }) => {
  const map = useMap();
  useEffect(() => {
    if (position) {
      map.setView(position);
    }
  }, [position, map]);
  return null;
};

const PlayerMap = () => {
  const [position, setPosition] = useState(null);
  const [playerPosition, setPlayerPosition] = useState(null);

  useEffect(() => {
    if (!navigator.geolocation) {
      alert("Geolocation not supported.");
      return;
    }

    const watchId = navigator.geolocation.watchPosition(
      (pos) => {
        const { latitude, longitude } = pos.coords;
        const newPos = [latitude, longitude];
        setPosition(newPos);
        setPlayerPosition(newPos); // Follow real movement
      },
      (err) => console.error(err),
      { enableHighAccuracy: true, maximumAge: 1000, timeout: 5000 }
    );

    return () => navigator.geolocation.clearWatch(watchId);
  }, []);

  return position ? (
    <div>
      <MapContainer 
      center={position}
      zoom={18}
      minZoom={16}   // <-- user can't zoom out past this
      maxZoom={20}   // <-- optional: restrict zooming in too far
      style={{ height: "80vh", width: "100%" }}
      >

        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        <Marker position={playerPosition} />
        <Circle center={position} radius={1000} color="blue" />
        <RecenterMap position={position} />
      </MapContainer>
    </div>
  ) : (
    <p>Loading location...</p>
  );
};

export default PlayerMap;
