// src/components/PlayerMap.jsx

import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Circle, useMap } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import socket from "../socket"; // ‚úÖ import your socket client

// Fix Leaflet marker icons
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: "https://unpkg.com/leaflet@1.9.3/dist/images/marker-icon-2x.png",
  iconUrl: "https://unpkg.com/leaflet@1.9.3/dist/images/marker-icon.png",
  shadowUrl: "https://unpkg.com/leaflet@1.9.3/dist/images/marker-shadow.png",
});

// Component to recenter map
const RecenterMap = ({ position }) => {
  const map = useMap();
  useEffect(() => {
    if (position) {
      map.setView(position);
    }
  }, [position, map]);
  return null;
};

const PlayerMap = () => {
  const [position, setPosition] = useState(null);
  const [playerPosition, setPlayerPosition] = useState(null);
  const [allPlayers, setAllPlayers] = useState({});
  const watchIdRef = useRef(null);

  useEffect(() => {
    if (!navigator.geolocation) {
      alert("Geolocation is not supported by your browser.");
      return;
    }

    watchIdRef.current = navigator.geolocation.watchPosition(
      (pos) => {
        const { latitude, longitude } = pos.coords;
        const newPos = [latitude, longitude];
        setPosition(newPos);
        setPlayerPosition(newPos);

        socket.emit("updatePosition", { lat: latitude, lng: longitude });
      },
      (err) => {
        console.error("‚ùå Geolocation error:", err);
        alert(`Geolocation error: ${err.message}`);
      },
      {
        enableHighAccuracy: true,
        timeout: 20000,
        maximumAge: 0,
      }
    );

    return () => {
      if (watchIdRef.current !== null) {
        navigator.geolocation.clearWatch(watchIdRef.current);
      }
    };
  }, []);

  useEffect(() => {
    socket.on("playersUpdate", (players) => {
      setAllPlayers(players);
    });

    return () => {
      socket.off("playersUpdate");
    };
  }, []);

  return position && playerPosition ? (
    <div>
      <MapContainer
  center={position}
  zoom={18}
  minZoom={15}
  maxZoom={20}
  style={{ height: "80vh", width: "100%" }}
>
  <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

  {/* ‚úÖ Show YOUR marker in red (custom icon) */}
  <Marker 
    position={playerPosition} 
    icon={L.icon({ 
      iconUrl: "https://maps.google.com/mapfiles/ms/icons/red-dot.png",
      iconSize: [32, 32],
      iconAnchor: [16, 32],
    })}
  />

  {/* ‚úÖ Show OTHER players */}
  {Object.entries(allPlayers).map(([id, loc]) => {
    // Skip yourself (optional if you want your own marker styled differently)
    if (loc.lat === playerPosition[0] && loc.lng === playerPosition[1]) return null;

    return (
      <Marker
        key={id}
        position={[loc.lat, loc.lng]}
        icon={L.icon({
          iconUrl: "https://maps.google.com/mapfiles/ms/icons/blue-dot.png",
          iconSize: [32, 32],
          iconAnchor: [16, 32],
        })}
      />
    );
  })}

  <Circle center={playerPosition} radius={1000} color="blue" />
  <RecenterMap position={position} />
</MapContainer>

    </div>
  ) : (
    <p>üìç Loading location‚Ä¶ Make sure location permission is allowed.</p>
  );
};

export default PlayerMap;
