// src/components/PlayerMap.jsx

import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Circle } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

// Fix Leaflet marker icons
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: "https://unpkg.com/leaflet@1.9.3/dist/images/marker-icon-2x.png",
  iconUrl: "https://unpkg.com/leaflet@1.9.3/dist/images/marker-icon.png",
  shadowUrl: "https://unpkg.com/leaflet@1.9.3/dist/images/marker-shadow.png",
});

const PlayerMap = () => {
  const [position, setPosition] = useState(null);
  const [playerPosition, setPlayerPosition] = useState(null);

  useEffect(() => {
    if (!navigator.geolocation) {
      alert("Geolocation not supported.");
      return;
    }

    const watchId = navigator.geolocation.watchPosition(
      (pos) => {
        const { latitude, longitude } = pos.coords;
        const newPos = [latitude, longitude];
        setPosition(newPos);
        setPlayerPosition(newPos);
      },
      (err) => console.error(err),
      { enableHighAccuracy: true }
    );

    return () => navigator.geolocation.clearWatch(watchId);
  }, []);

  const movePlayer = (deltaLat, deltaLng) => {
    if (!position) return;
    const newLat = playerPosition[0] + deltaLat;
    const newLng = playerPosition[1] + deltaLng;
    const distance = L.latLng(position).distanceTo([newLat, newLng]);

    if (distance <= 1000) {
      setPlayerPosition([newLat, newLng]);
    } else {
      alert("Out of bounds! (1000m radius)");
    }
  };

  return position ? (
    <div>
      <MapContainer center={position} zoom={18} style={{ height: "80vh", width: "100%" }}>
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <Marker position={playerPosition} />
        <Circle center={position} radius={1000} color="blue" />
      </MapContainer>

      <div style={{ display: "flex", justifyContent: "center", marginTop: "10px" }}>
        <button onClick={() => movePlayer(0.0001, 0)}>⬆️</button>
      </div>
      <div style={{ display: "flex", justifyContent: "center" }}>
        <button onClick={() => movePlayer(0, -0.0001)}>⬅️</button>
        <button onClick={() => movePlayer(0, 0.0001)}>➡️</button>
      </div>
      <div style={{ display: "flex", justifyContent: "center" }}>
        <button onClick={() => movePlayer(-0.0001, 0)}>⬇️</button>
      </div>
    </div>
  ) : (
    <p>Loading location...</p>
  );
};

export default PlayerMap;
